import React from 'react'
import { useNavigation } from '@react-navigation/native'
import { TouchableOpacity, Image } from 'react-native'
import RefrescarAsset from 'assets/refresh.png'

const useRefrescar = (refrescar) => {
    const navigation = useNavigation()

    React.useEffect(() => {
        navigation.setOptions({
            headerRight: () => {
                return (
                    <TouchableOpacity
                        style={{
                            paddingRight: 12,
                        }}
                        onPress={refrescar}
                    >
                        <Image
                            source={RefrescarAsset}
                            style={{
                                width: 24,
                                height: 24,
                            }}
                        />
                    </TouchableOpacity>
                )
            }
        })
    }, [navigation])

    return null
}

export default useRefrescar
