import React from 'react';
import { Container, Text, Spacer } from '../../component'
import styles from './styles'

const cartItemsMock = [{
    id: 1,
    productDescription: 'CEPILLO',
    quantity: 1,
    price: 20,
}, {
    id: 2,
    productDescription: 'PASTA',
    quantity: 2,
    price: 40,
}]

const PDV = () => {
    const [cartItems, setCartItems] = React.useState(cartItemsMock)

    const calculateTotal = () => {
        return cartItems.reduce((carry, value) => {
            return carry + value.price
        }, 0)
    }

    const handleDelete = (item) => () => {
        alert(JSON.stringify(item))
    }

    const handleFinish = () => {

    }

    return (
        <Container flex spaceBetween>
            <Container row spaceBetween padded>
                <Container>
                    {cartItems.map(item => (
                        <Container row style={{
                            width: 500
                        }} spaceBetween>
                            <Container row alignCenter>
                                <Text.TitleH1>{item.quantity}</Text.TitleH1>
                                <Spacer.Small />
                                <Container alignCenter justifyCenter style={{
                                    width: 4,
                                    height: 4,
                                    backgroundColor: 'gold',
                                    borderRadius: 24,
                                }} />
                                <Spacer.Small />
                                <Text.TitleH1>
                                    {item.productDescription}
                                </Text.TitleH1>
                            </Container>
                            <Container row alignCenter>
                                <Text.TitleH1>
                                    {item.price} ARS
                            </Text.TitleH1>
                                <Spacer.Medium />
                                <Container onPress={handleDelete(item)}>
                                    <Text.TitleH3>Quitar</Text.TitleH3>
                                </Container>
                            </Container>
                        </Container>
                    ))}
                </Container>
                <Container>
                    <Text.TitleH1>{calculateTotal()} ARS</Text.TitleH1>
                </Container>
            </Container>
            <Container alignCenter>
                <Container onPress={handleFinish}>
                    <Text.TitleH1>Finalizar</Text.TitleH1>
                </Container>
                <Spacer.Medium />
            </Container>
        </Container>
    );
}

export default PDV
