import { StyleSheet } from 'react-native'
import colors from 'constants/colors'

const styles = StyleSheet.create({
    placeholderStyle: {
        fontSize: 16,
        color: colors.text,
    },
    selectedTextStyle: {
        fontSize: 16,
        color: colors.text
    },
    inputSearchStyle: {
        height: 40,
        fontSize: 16,
    },
    dropdown: {
        height: 50,
        width: 240,
        borderColor: colors.text,
        borderWidth: 0.5,
        borderRadius: 8,
        paddingHorizontal: 8,
    },
    dropdownFocused: {
        borderColor: colors.text
    },
    dropdownContainer: {
        backgroundColor: colors.primary
    }
})

export default styles
