import { StyleSheet } from 'react-native'
import colors from 'constants/colors'

const styles = StyleSheet.create({
    avatar: {
        width: 120,
        height: 120,
        borderRadius: 80,
        borderColor: colors.text,
        borderWidth: .5,
    },
    picker: {
        height: 50,
        width: '100%',
        color: colors.text
    },
    pickerItem: {
        color: colors.text
    },
    drawerItemContainer: {
        backgroundColor: "rgba(0,0,0,0.5)",
        padding: 12,
    },
    drawerItemIcon: {
        width: 16,
        height: 16,
    },

})

export default styles
