import React from 'react'
import { useNavigation } from '@react-navigation/native'
import useStore from 'services/store'
import { Container, Text } from 'component'

const useIglesiaHeader = (moduloActual) => {
    const navigation = useNavigation()
    const iglesia = useStore(state => state.iglesia)

    React.useEffect(() => {
        navigation.setOptions({
            title: (
                <Container alignCenter>
                    <Text.Small>{iglesia?.nombre}</Text.Small>
                    <Text.BodyBold>{moduloActual}</Text.BodyBold>
                </Container>
            ),
        })
    }, [navigation, iglesia])

    return null
}

export default useIglesiaHeader
