import React from 'react';
import { Container, Text, Spacer } from '../../component'



const PDV = () => {
    const [cartItems, setCarts] = React.useState([{
        productDescription: 'CEPILLO',
        quantity: 1,
        price: 20,
    }, {
        productDescription: 'PASTA',
        quantity: 2,
        price: 40,
    }])
    React.useEffect(() => {

    }, [])

    console.log(cartItems.reduce((value, carry) => (
        carry + value.price
    ), 0))

    return (
        <Container row spaceBetween padded>
            <Container>
                {cartItems.map(item => (
                    <Container row alignCenter>
                        <Text.TitleH1>{item.quantity}</Text.TitleH1>
                        <Spacer.Small />
                        <Container alignCenter justifyCenter style={{
                            width: 4,
                            height: 4,
                            backgroundColor: 'gold',
                            borderRadius: 24,
                        }} />
                        <Spacer.Small />
                        <Text.TitleH1>
                            {item.productDescription}
                        </Text.TitleH1>
                    </Container>
                ))}
            </Container>
            <Container>
                <Text.TitleH1>{
                    cartItems.reduce((value, carry) => (
                        carry + value.price
                    ), 0)
                }</Text.TitleH1>
            </Container>
        </Container>
    );
}

export default PDV
