import React from 'react'
import { useNavigation } from '@react-navigation/native'

const useOcultarHeader = () => {
    const navigation = useNavigation()

    React.useEffect(() => {
        navigation.setOptions({
            headerShown: false,
        })
    }, [navigation])

    return null
}

export default useOcultarHeader
