import React, { useCallback } from 'react';
import { Alert } from 'react-native'
import { Container, Text, Spacer } from '../../component'
import styles from './styles'
import { dialog } from 'electron'
import { useBackButton, useScanner, useHeaderTitle } from '../../hook'

const inventoryMock = [{
    id: 1,
    productId: 4,
    barcode: '111',
    description: 'CEPILLO',
    price: 20,
}, {
    id: 2,
    productId: 5,
    barcode: '222',
    description: 'JABON',
    price: 40,
}]

const PDV = () => {
    const [cartItems, setCartItems] = React.useState([])
    const [scanned, setScanned] = React.useState(null)

    useHeaderTitle("Punto de venta")
    useBackButton()
    useScanner(useCallback((barcode) => {
        const item = inventoryMock.find(item => (
            item.barcode === barcode
        ))
        if (!item) {
            alert(barcode + ' no encontrado')
        }
        else {
            setScanned({
                ...item,
                scannedAt: Date.now(),
            })
        }
    }, [setScanned]))

    React.useEffect(() => {
        if (!scanned) {
            return
        }
        updateCart(scanned)
    }, [scanned])

    const updateCart = (scannedProduct) => {
        const existingItem = cartItems.find(item => (
            item.productId === scannedProduct.productId
        ))

        if (existingItem) {

            setCartItems(cartItems.map(item => (
                item.productId === scannedProduct.productId ?
                    { ...item, quantity: item.quantity + 1 } : item
            )))
        }
        else {
            setCartItems([
                ...cartItems,
                {
                    id: cartItems.length + 1,
                    quantity: 1,
                    ...scannedProduct,
                }
            ])
        }
    }

    const calculateTotal = () => {
        return cartItems.reduce((carry, value) => {
            return carry + (value.price * value.quantity)
        }, 0)
    }

    const handleDelete = (item) => () => {
        if (item.quantity === 1) {
            setCartItems(cartItems.filter(cartItem => (
                cartItem.productId !== item.productId
            )))
            return
        }

        setCartItems(
            cartItems.map(cartItem => (
                cartItem.productId === item.productId ?
                    { ...cartItem, quantity: cartItem.quantity - 1 } : cartItem
            ))
        )
    }

    const handleFinish = () => {
        setCartItems([])

    }

    return (
        <Container flex spaceBetween>
            <Container row spaceBetween padded>
                <Container fullWidth>
                    {!cartItems.length &&
                        <Text.Body>Comience a escanear...</Text.Body>}
                    {cartItems.map(item => (
                        <Container flex row spaceBetween>
                            <Container style={{
                                width: 256,
                            }}>
                                <Text.Small>PRODUCTO</Text.Small>
                                <Text.TitleH1>
                                    {item.description}
                                </Text.TitleH1>
                            </Container>
                            <Container>
                                <Text.Small>PRECIO x CANTIDAD</Text.Small>
                                <Container row alignCenter>
                                    <Text.TitleH1>
                                        {item.price} ARS
                                    </Text.TitleH1>
                                    <Spacer.Small />
                                    <Text.Body>X</Text.Body>
                                    <Spacer.Small />
                                    <Text.TitleH1>{item.quantity}</Text.TitleH1>
                                </Container>
                            </Container>
                            <Container>
                                <Text.Small>SUBTOTAL</Text.Small>
                                <Container row alignCenter>
                                    <Text.TitleH1>
                                        {item.price * item.quantity} ARS
                                </Text.TitleH1>
                                    <Spacer.Medium />
                                    <Container onPress={handleDelete(item)}>
                                        <Text.TitleH3>Quitar</Text.TitleH3>
                                    </Container>
                                </Container>
                            </Container>
                        </Container>
                    ))}
                </Container>

            </Container>
            <Container row alignEnd spaceBetween padded>
                <Container>
                    <Text.Small>TOTAL</Text.Small>
                    <Text.TitleH1>{calculateTotal()} ARS</Text.TitleH1>
                </Container>
                <Container onPress={handleFinish}>
                    <Text.TitleH1>Finalizar</Text.TitleH1>
                </Container>
            </Container>
        </Container>
    );
}

export default PDV
