import React from 'react';
import { Image, ImageBackground, TextInput } from 'react-native';
import { Container, Spacer, Text, Button } from 'component'
import logoAsset from 'assets/ccr.jpeg'
import iconAsset from 'assets/facebook-icon.png'
import { getDatabase, ref as dbRef, set } from 'firebase/database';
import {
    useFacebook,
    useOcultarHeader
} from 'hook'
import styles from './styles'

const Auth = ({ navigation }) => {
    useOcultarHeader()

    const handlePressAsync = async () => {        
        const db = getDatabase();
        const reference = dbRef(db, 'usuarios/123');
        set(reference, {
            id: 123,
            name: 'Esteban',
        });
        navigation.navigate('Home')
    };

    return (
        <Container flex justifyCenter alignCenter padded>
            <Text.TitleH1>CMS</Text.TitleH1>
            <Spacer.Large />
            <Container>
                <Text.Small>Email</Text.Small>
                <Spacer.Small />
                <TextInput
                    style={styles.input}
                />
            </Container>
            <Container>
                <Spacer.Medium />
                <Text.Small>Password</Text.Small>
                <Spacer.Small />
                <TextInput
                    secureTextEntry
                    style={styles.input}
                />
                <Spacer.Medium />
            </Container>
            <Spacer.Large />
            <Button.Primary
                backgroundColor="whitesmoke"
                textColor="black"
                width={240}
                title="Ingresar"
                onPress={handlePressAsync}
            />
            {/*<Button.Primary
                    backgroundColor="whitesmoke"
                    textColor="black"
                    title={usuario?.name ?
                        copies.Auth.ContinueWithFacebookAs.replace('{name}', usuario?.name) :
                        copies.Auth.ContinueWithFacebook}
                    onPress={handlePressAsync}
                    renderLeftIcon={() => (
                        <Image
                            source={iconAsset}
                            style={styles.icon}
                        />
                    )}
                    renderRightIcon={() => null}
                    />*/}
            <Spacer.Medium />
        </Container>
    );
}

export default Auth
