import React from 'react';
import { Container, Text, Spacer } from '../../component'

const cartItemsMock = [{
    productDescription: 'CEPILLO',
    quantity: 1,
    price: 20,
}, {
    productDescription: 'PASTA',
    quantity: 2,
    price: 40,
}]

const PDV = () => {
    const [cartItems, setCartItems] = React.useState(cartItemsMock)
    React.useEffect(() => {

    }, [])

    const calculateTotal = () => {
        return cartItems.reduce((carry, value) => {
            return carry + value.price
        }, 0)
    }

    return (
        <Container row spaceBetween padded>
            <Container>
                {cartItems.map(item => (
                    <Container row style={{
                        width: 500
                    }} spaceBetween>
                        <Container row alignCenter>
                            <Text.TitleH1>{item.quantity}</Text.TitleH1>
                            <Spacer.Small />
                            <Container alignCenter justifyCenter style={{
                                width: 4,
                                height: 4,
                                backgroundColor: 'gold',
                                borderRadius: 24,
                            }} />
                            <Spacer.Small />
                            <Text.TitleH1>
                                {item.productDescription}
                            </Text.TitleH1>
                        </Container>
                        <Container row alignCenter>
                            <Text.TitleH1>
                                {item.price} ARS
                            </Text.TitleH1>
                            <Spacer.Medium />
                            <Container>
                                <Text.TitleH3>Quitar</Text.TitleH3>
                            </Container>
                        </Container>
                    </Container>
                ))}
            </Container>
            <Container>
                <Text.TitleH1>{calculateTotal()} ARS</Text.TitleH1>
            </Container>
        </Container>
    );
}

export default PDV
