import React, { useState } from 'react';
import { TextInput, ScrollView } from 'react-native'
import { useRoute } from '@react-navigation/native'
import { Container, Text, Spacer, Button } from 'component'
import useStore from 'services/store'
import { useBackButton } from 'hook'
import { camelize } from 'services/strings'
import styles from './styles'

const ClusterAddField = ({ onPress }) => {
    const handlePress = () => {
        onPress()
    }

    return (
        <Container padded onPress={handlePress}>
            <Container style={{
                borderColor: 'white',
                borderWidth: 1,
                borderRadius: 8,
            }}>
                <Container padded row spaceBetween justifyCenter>
                    <Container>
                        <Text.Body color="white">âž• AGREGAR CAMPO</Text.Body>
                    </Container>
                    <Container>

                    </Container>
                </Container>
            </Container>
        </Container>
    )
}

const ModelAdd = () => {
    const [modelName, setModelName] = useState(null)
    const route = useRoute()

    useBackButton()

    const handleModelNameChange = (text) => setModelName(text)
    const openModal = useStore(state => state.openModal)

    const key = route?.params?.key
    const model = route?.params?.model

    return (
        <Container flex spaceBetween>
            <Container>
                {model ?
                    <Container padded>
                        <Text.TitleH3>({key})</Text.TitleH3>
                    </Container> :
                    <Container paddingX={1}>
                        <Spacer.Large />
                        <Text.Small>TITULO</Text.Small>
                        <Spacer.Small />
                        <TextInput
                            style={styles.input}
                            value={modelName}
                            defaultValue={modelName}
                            placeholder="Nombre del modelo..."
                            returnKeyType="done"
                            onChangeText={handleModelNameChange}
                        />
                        <Spacer.Small />
                        <Text.Body>ID: {camelize(!!modelName?.length ? modelName : '-')}</Text.Body>
                        <Spacer.Small />
                    </Container>}

                <ClusterAddField
                    onPress={() => {
                        if (!key) {
                            if (!modelName?.length) {
                                alert('Por favor ingresa el nombre del modelo')
                                return
                            }
                        }

                        openModal('Donate', {
                            modelName: modelName || key,
                            modelSlug: camelize(modelName) || key
                        })
                    }}
                />

                {model && <Container>
                    <Container padded>
                        <Text.Small>CAMPOS</Text.Small>
                        <Spacer.Medium />
                    </Container>
                    <ScrollView showsVerticalScrollIndicator={false}>
                        <Container paddingX={1}>
                            {Object.keys(model).map((key, index) => (
                                <Container key={model[key].modelSlug}>
                                    <Text.Body>{index}. {model[key].fieldName} ({model[key].fieldSlug})</Text.Body>
                                    <Spacer.Medium />
                                </Container>
                            ))}
                        </Container>
                    </ScrollView>
                </Container>}
            </Container>

            <Container alignCenter padded>
                <Button.Primary title="GUARDAR" />
                <Spacer.Large />
            </Container>
        </Container>
    );
}

export default ModelAdd
